export const SkillProgress = ({ lang, value }: SkillProgressProps) => {
  const [count, setcount] = useState(0);

  useEffect(() => {
    inView(`.skillProgress`, () => {
      setcount(value);
    });
  }, []);

  return (
    <Stack alignItems="center" width="50px" className="skillProgress">
      <Stack
        sx={{
          position: 'relative',
          alignItems: 'center',
          justifyContent: 'center',
          width: 'fit-content',
        }}
      >
        <CircularProgress
          variant="determinate"
          sx={{
            color: '#efefef',
          }}
          size={40}
          thickness={4}
          value={100}
        />
        <CircularProgress
          size={40}
          color="primary"
          variant="determinate"
          value={count}
          sx={{
            position: 'absolute',
            left: 0,
            '& circle': {
              backgroundColor: 'gray',
              transition: '1.5s',
              strokeLinecap: 'round',
            },
          }}
        />

        <Stack sx={{ position: 'absolute', width: '20px' }}>
          {devIcons[lang].icon}
        </Stack>
      </Stack>
      <Stack mt={'5px'} sx={{ fontSize: '10px' }}>
        {devIcons[lang].label}
      </Stack>
    </Stack>
  );
};
